CREATE VIEW "ia_enterprise_form_item_selectorcontent_view" AS select fi._id, fi.itemId, fi.formId, fi.formTypeId, fi.enterpriseId, fi.itemName, fi.itemPhysicalName, fi.itemType, fi.isMustWrite, fi.isCapital, fi.formula, group_concat(fi.info, '~~') as selectorContents from (select fi.*, fisc.info from ia_form_item as fi left join ia_form_item_selectorcontent as fisc on fi._id=fisc.formItemRowId order by fisc.selectorcontentFakeId) as fi group by fi._id