CREATE VIEW "ia_user_enterprise_todotask_advice_view" AS select uetdt._id, uetdt.taskId, uetdt.enterpriseId, uetdt.approveNumber, uetdt.taskTitle, uetdt.applicantName, uetdt.taskSenderFakeId, uetdt.taskStatus, uetdt.taskSubmitTimestamp, group_concat(uetdt.advisorId, '~~') as advisorIds, group_concat(uetdt.advisorName, '~~') as advisorNames, group_concat(uetdt.adviceState, '~~') as adviceStates, group_concat(uetdt.adviceContent, '~~') as adviceContents, group_concat(uetdt.adviceGivenTimestamp, '~~') as adviceGivenTimestamps from (select uetdt.*, tdta.advisorId, tdta.advisorName, tdta.adviceState, tdta.adviceContent, tdta.adviceGivenTimestamp from ia_user_enterprise_todotask as uetdt left join ia_todotask_advice as tdta on uetdt._id=tdta.todoTaskRowId order by tdta.adviceFakeId) as uetdt group by uetdt._id order by uetdt.taskSubmitTimestamp desc